mapper.insert:
insert into questions ( course_id, question ,deadline
  )
values ( #{courseId,jdbcType=INTEGER}, #{question,jdbcType=LONGVARCHAR},#{deadline,jdbcType=TIMESTAMP}
  )

mapper.update:
update questions
set course_id = #{courseId,jdbcType=INTEGER},
  question = #{question,jdbcType=LONGVARCHAR},
  deadline= #{deadline,jdbcType=TIMESTAMP}
where question_id = #{questionId,jdbcType=INTEGER}

mapper.select:
select question_id, course_id, question ,deadline,dup_check
from questions
where question_id = #{questionId,jdbcType=INTEGER}

select question_id, course_id, question ,deadline,dup_check
from questions
where course_id=#{courseId}

mapper.update:
update questions
set dup_check=#{dupCheck}
where question_id=#{questionId}
